$(document).ready(function() {
  cdn = "https://d2woyw6vp5g0b2.cloudfront.net/";
  $("img").each(function() {
    $(this).attr("src", cdn + $(this).attr("src"));
  });
});
$(document).ready(function() {
  $(".hover").hover(
    function() {
      $(this).addClass("flip");
    },
    function() {
      $(this).removeClass("flip");
    }
  );
});
